import React from 'react';
import { Button } from 'react-bootstrap';
import { Link } from 'react-router-dom';
import './Product.css'


const Product = ({product}) => {
    const{_id,job_title,company_name,location,type_of_job,time,job_description,skills,total_employee,company_logo}=product;
    return (
        <div>
        <div className="product m-2 p-3">
        <h6 className="text-start text-light m-2"><span className="py-1 px-2 customround">{type_of_job}</span></h6>
        <div className='d-flex'>
        <img className="w-50 image" src={company_logo} alt="" />
        <div className="details">
        <h3>{job_title}</h3>
        <div className='d-flex justify-content-center'><h6 className='d-inline'>{company_name}</h6>
        <h6 className='d-inline ms-5'>{location}</h6></div>
        <h6>{time}</h6>
        <h6 className="text-success">{total_employee} Employee</h6>
        <Link to={`/confirmorder/${_id}`}>
        <Button className="px-4 custom-btn">Details</Button>
        </Link>
        </div>
        </div>
       
    </div>
    </div>
    );
};

export default Product;
