import { faCog, faPencilAlt,faSearch, faShoppingCart } from '@fortawesome/free-solid-svg-icons';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import React from 'react';
import { Link } from 'react-router-dom';
import { Container, Nav, Navbar, NavDropdown, NavItem } from 'react-bootstrap';
import logo from '../../../images/4.png'
import useAuth from '../../Firebase/useAuth';
const Header = () => {
  const {user,logOut}=useAuth();
    return (
        <div>
            <Navbar bg="light" expand="lg">
  <Container className="py-2">
    <Navbar.Brand><Link className="nav-link " to="/"><img width="55%" src={logo} alt="" /></Link></Navbar.Brand>
    <Navbar.Toggle aria-controls="basic-navbar-nav" />
    <Navbar.Collapse id="basic-navbar-nav">
      <Nav className="mx-auto">
        <Nav.Link><Link className="nav-link " to="/home">HOME<Link/></Link></Nav.Link>
        <Nav.Link><Link className="nav-link " to="/products">EXPLORE<Link/></Link></Nav.Link>
        <Nav.Link><Link className="nav-link " to="/reviews">REVIEWS<Link/></Link></Nav.Link>
        
        {/* <Nav.Link>REVIEWS</Nav.Link> */}
        <NavDropdown className="me-5 nav-link " title="ADMIN" id="basic-nav-dropdown">
          <NavDropdown.Item>CREATE ADMIN</NavDropdown.Item>
          <NavDropdown.Item>CREATE PRODUCT</NavDropdown.Item>
          <NavDropdown.Item>MANAGE PRODUCT</NavDropdown.Item>
          <NavDropdown.Item>CHECKOUT ALL ORDER</NavDropdown.Item>
          <NavDropdown.Divider />
          <NavDropdown.Item>Separated link</NavDropdown.Item>
        </NavDropdown>
        <Nav.Link><Link className="nav-link" to="/"><FontAwesomeIcon icon={faSearch} /></Link></Nav.Link>
        <Nav.Link><Link className="nav-link " to="/mycart"><FontAwesomeIcon icon={faShoppingCart} /></Link></Nav.Link>
        <NavDropdown className="nav-link " title={<FontAwesomeIcon icon={faCog} />} id="basic-nav-dropdown">
        
          <NavDropdown.Item className="fs-5" disabled>MY ACCOUNT</NavDropdown.Item>
          <NavDropdown.Item><Link to="/signin" className="nav-link">SIGN IN<Link/></Link></NavDropdown.Item>
          <NavDropdown.Item><Link to="/signup" className="nav-link">SIGN UP<Link/></Link></NavDropdown.Item>
          <NavDropdown.Item><Link to="/mycart" className="nav-link">MY CART<Link/></Link></NavDropdown.Item>
          {/* <NavDropdown.Item>SIGN UP</NavDropdown.Item>
          <NavDropdown.Item>MY CART</NavDropdown.Item> */}
        </NavDropdown>
        <Navbar.Brand href="#"> <NavItem>   <Link  to="/"><img width="40px" className="rounded-circle" src={user.photoURL} alt="" /></Link> </NavItem> </Navbar.Brand>
      </Nav>
      <Nav>
      <NavItem >   <Link className="nav-link "   to="/signup">Sign Up</Link> </NavItem>

{user?.email ?
                      
                      <Button onClick={logOut} className="text-danger" variant="light"> Logout</Button> :
                      <Nav.Link as={Link} to="/signin"> Sign In</Nav.Link>}

      </Nav>
    </Navbar.Collapse>
  </Container>
</Navbar>
<p className="bg-secondary text-light py-2 my-0"><FontAwesomeIcon icon={faPencilAlt} /> Overnight Domestic Shipping Starting At $87.99! View Details !</p>
        </div>
    );
};

export default Header;
