import React, { useEffect, useState } from 'react';
import { Carousel, Nav } from 'react-bootstrap';
import { Link } from 'react-router-dom';
import CreateReview from '../../Components/CreateReview/CreateReview';
import Review from '../../Components/Review/Review';

const Reviews = () => {
    const [product , setProduct]=useState([]);
    useEffect(()=>{
        fetch('http://localhost:5000/reviews')
        .then(res=>res.json())
        .then(data=>setProduct(data))
    },[])
    return (
        <div className="my-5 py-5">
            <h1 className=" text-secondary">What Our <span className="fw-light text-danger">Clients Say</span></h1>
            <p className=" mb-5">Welcome to my personal presentation</p>
        <Carousel className="bg-secondary py-5">
        {
            product.map(product =><Carousel.Item>
                <Review
                key={product._id}
                product={product}
            ></Review>
            </Carousel.Item>
            )
        }
        </Carousel>
        <Nav.Link><Link className="nav-link bg-secondary" to="/createreview">I Want To Provide a Feedback<Link/></Link></Nav.Link>
        <CreateReview></CreateReview>
        </div>
    );
};

export default Reviews;
