import axios from 'axios';
import React from 'react';
import { useForm } from "react-hook-form";
import useAuth from '../../Firebase/useAuth';
import './CreateReview.css';


const CreateReview = () => {
    const{user}=useAuth();
    const { register, handleSubmit, reset } = useForm();

    const onSubmit = data => {
        console.log(data);

        axios.post('http://localhost:5000/reviews', data)
            .then(res => {
                if (res.data.insertedId) {
                    alert('Successfully Commented');
                    reset();
                }
            })
    }

    return (

        <div>
           
        <form className="mb-5" onSubmit={handleSubmit(onSubmit)}>
           <div className="row">
           <div className="col-3">
            <img src={user.photoURL} className="w-25 my-3 rounded-circle" alt="" />
            </div>
            <div className="col-9">
            <textarea {...register("comment",{ required: true, maxLength: 30 })} placeholder="Whats On Your Mind?" className="mt-4" />
            </div>
           </div>
            <input className="btn btn-primary d-block mx-auto" type="submit" value="Post" />
        </form>
        </div>
    );
};

export default CreateReview;
